import React, {useState} from 'react';
import './App.css'; 

function App() {
  const [width, setWidth] = useState(8)
  const [height, setHeight] = useState(4)
  const [numBoxes, setNumBoxes] = useState(8)
  const [minSize, setMinSize] = useState(1)
  const [maxSize, setMaxSize] = useState(1)

  const [field, setField] = useState([]);

  const createField = (width, height) => {
    const newField = Array.from({ length: height }, () =>
      Array.from({ length: width }, () => '*1')
    );
    return newField;
  };

  // Function to convert a string to a color
  const stringToColor = async (string) => {
    const hashBuffer = await crypto.subtle.digest('SHA-256', new TextEncoder().encode(string));
    const hashArray = Array.from(new Uint8Array(hashBuffer));
    const hashHex = hashArray.map((b) => b.toString(16).padStart(2, '0')).join('');
    const r = parseInt(hashHex.substring(0, 6), 16) % 256;
    const g = parseInt(hashHex.substring(6, 12), 16) % 256;
    const b = parseInt(hashHex.substring(12, 18), 16) % 256;
    return `rgb(${r}, ${g}, ${b})`;
  };

  // Function to visualize the field
  const visualizeField = async (field) => {
    const visualizedField = [];
    field.forEach((row, i) => {
      const rowElements = row.map(async (item, j) => {
        const color = item === '**' ? 'black' : await stringToColor(item);
        return (
          <div
            key={`${i}-${j}`}
            style={{
              width: '3em',
              height: '3em',
              backgroundColor: color,
              padding: '1em',
              margin: '0em',
              border: '1px solid black',
              display: 'inline-block',
            }}
          />
        );
      });
      visualizedField.push(<div key={i}>{rowElements}</div>);
    });
    return visualizedField;
  };

  // Create initial field when component mounts
  React.useEffect(() => {
    const initialField = createField(width, height);
    visualizeField(initialField).then((visualized) => setField(visualized));
  }, [width, height]);

  return (
    <div className="app">

      <div className="heading">
        <h3> 弁当屋 🍱 </h3> 
      </div>

      <div className="inputs">
        {/* Four Number Input Fields */}
        <div>
          <label htmlFor="width">Width:</label>
          <br />
          <input type="number" id="width" placeholder={width} value={width} min="2" />
        </div>
        <div>
          <label htmlFor="height">Height:</label>
          <br />
          <input type="number" id="height" placeholder={height} value={height} min="2"/>
        </div>
        <div>
          <label htmlFor="input3">Num Boxes:</label>
          <br />
          <input type="number" id="input3" placeholder={numBoxes} value={numBoxes} min="1"/>
        </div>
        <div>
          <label htmlFor="input4">Min Size:</label>
          <br />
          <input type="number" id="input4" placeholder={minSize} value={minSize} min="1"/>
        </div>
        <div>
          <label htmlFor="input5">Max Size:</label>
          <br />
          <input type="number" id="input5" placeholder={maxSize} value={maxSize} min="1"/>
        </div>

        <button> Generate </button>
      </div>

      <div className="bentoArea">
        {field}
      </div>

    </div>
  );
}

export default App;
